    observers_.NotifyObservers(&TabStripModelObserver::OnTabAdded,
                              this, raw_contents, index, foreground);
    
    // Update selection if necessary
    if (foreground) {
      SetActiveIndex(index);
    }
  }
  
  // Enhanced removal with detailed reason tracking
